@partial(jit, static_argnums=2)
def exponential_decay_function(initial_value, rate_decay, max_t):
    decayed_function = jnp.repeat(initial_value, max_t)
    return decayed_function * jnp.power(1 - rate_decay, jnp.arange(max_t))

